import{_ as e,o as r,c as a,M as t}from"./chunks/framework.dbe94cdc.js";const u=JSON.parse('{"title":"在应用中接入微软登录","description":"","frontmatter":{},"headers":[],"relativePath":"大前端/第三方登录/01.集成微软登录.md","lastUpdated":1692887580000}'),o={name:"大前端/第三方登录/01.集成微软登录.md"},c=t('<h1 id="在应用中接入微软登录" tabindex="-1">在应用中接入微软登录 <a class="header-anchor" href="#在应用中接入微软登录" aria-label="Permalink to &quot;在应用中接入微软登录&quot;">​</a></h1><p>微软登录是通过<a href="https://azure.microsoft.com/en-us/products/active-directory/" target="_blank" rel="noreferrer">azure active directory</a>，而代码则是通过微软的身份验证库<a href="https://github.com/AzureAD/microsoft-authentication-library-for-js" target="_blank" rel="noreferrer">MSAL</a></p><h2 id="先决条件" tabindex="-1">先决条件 <a class="header-anchor" href="#先决条件" aria-label="Permalink to &quot;先决条件&quot;">​</a></h2><ul><li>一个<a href="https://learn.microsoft.com/zh-cn/azure/active-directory/fundamentals/whatis#what-are-the-azure-ad-licenses" target="_blank" rel="noreferrer">Azure Active Directory许可证</a></li></ul><h2 id="electron中接入微软登录" tabindex="-1">electron中接入微软登录 <a class="header-anchor" href="#electron中接入微软登录" aria-label="Permalink to &quot;electron中接入微软登录&quot;">​</a></h2><p><a href="https://learn.microsoft.com/zh-cn/azure/active-directory/develop/quickstart-desktop-app-nodejs-electron-sign-in" target="_blank" rel="noreferrer">快速入门：从 Node.js 桌面应用中让用户登录并调用 Microsoft Graph</a></p><h3 id="登录流程" tabindex="-1">登录流程 <a class="header-anchor" href="#登录流程" aria-label="Permalink to &quot;登录流程&quot;">​</a></h3><p>按钮点击 -&gt; 发送登录IPC -&gt; 主程序中IPC接收 -&gt; 获取交换证明密钥(PKCE) -&gt; 根据PKCE信息生成授权URL -&gt; 新建授权窗口并加载授权URL -&gt; 等待授权窗口返回登录结果 -&gt; 根据返回登录结果获取用户账户信息 -&gt; 向渲染程序发送IPC并带上账号信息 -&gt; 渲染程序接收IPC并处理登录信息</p><h2 id="参考文档" tabindex="-1">参考文档 <a class="header-anchor" href="#参考文档" aria-label="Permalink to &quot;参考文档&quot;">​</a></h2><p><a href="https://learn.microsoft.com/zh-cn/azure/active-directory/fundamentals/whatis" target="_blank" rel="noreferrer">Azure Active Directory简介</a></p><p><a href="https://learn.microsoft.com/zh-cn/azure/active-directory/develop/msal-overview" target="_blank" rel="noreferrer">Microsoft 身份验证库 (MSAL) 的概述</a></p>',11),i=[c];function n(l,s,h,d,_,f){return r(),a("div",null,i)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
